import React, { useState, useEffect } from 'react';
import { questionService, subjectService } from '../services/api';
import { toast } from 'react-toastify';

const Questions = () => {
  const [questions, setQuestions] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    subject: '',
    topic: ''
  });
  const [loading, setLoading] = useState(true);
  const [selectedQuestion, setSelectedQuestion] = useState(null);

  useEffect(() => {
    fetchQuestions();
    fetchSubjects();
  }, []);

  const fetchQuestions = async () => {
    try {
      const response = await questionService.getAllQuestions();
      setQuestions(response.data);
    } catch (error) {
      toast.error('Failed to fetch questions');
    } finally {
      setLoading(false);
    }
  };

  const fetchSubjects = async () => {
    try {
      const response = await subjectService.getAllSubjects();
      setSubjects(response.data);
    } catch (error) {
      toast.error('Failed to fetch subjects');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await questionService.createQuestion(formData);
      toast.success('Question posted successfully');
      setFormData({ title: '', description: '', subject: '', topic: '' });
      setShowForm(false);
      fetchQuestions();
    } catch (error) {
      toast.error('Failed to post question');
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  return (
    <div className="container" style={{ marginTop: '30px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>Q&A Forum</h1>
        <button onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancel' : '+ Ask Question'}
        </button>
      </div>

      {showForm && (
        <div className="card" style={{ marginBottom: '30px' }}>
          <h2>Ask a Question</h2>
          <form onSubmit={handleSubmit}>
            <label>Title</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              required
            />

            <label>Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              required
              rows="5"
            />

            <label>Subject</label>
            <select
              value={formData.subject}
              onChange={(e) => setFormData({...formData, subject: e.target.value})}
              required
            >
              <option value="">Select a subject</option>
              {subjects.map(subject => (
                <option key={subject._id} value={subject._id}>
                  {subject.name}
                </option>
              ))}
            </select>

            <button type="submit">Post Question</button>
          </form>
        </div>
      )}

      <div style={{ display: 'grid', gap: '15px' }}>
        {questions && questions.map(question => (
          <div
            key={question._id}
            className="card"
            style={{ cursor: 'pointer' }}
            onClick={() => setSelectedQuestion(question._id)}
          >
            <div>
              <h3>{question.title}</h3>
              <p>{question.description.substring(0, 150)}...</p>
              <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <span className={`badge badge-${question.status === 'answered' ? 'success' : 'primary'}`}>
                  {question.status}
                </span>
                <span className="badge badge-primary">
                  üë§ {question.asker.name}
                </span>
                <span className="badge badge-primary">
                  üëÅÔ∏è {question.views} views
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {questions.length === 0 && (
        <div className="alert alert-info">
          No questions yet. Be the first to ask!
        </div>
      )}
    </div>
  );
};

export default Questions;
