import React, { useState, useContext, useEffect } from 'react';
import { planService } from '../services/api';
import { AuthContext } from '../context/AuthContext';
import { toast } from 'react-toastify';

const StudyPlanner = () => {
  const [plans, setPlans] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    fetchPlans();
  }, []);

  const fetchPlans = async () => {
    try {
      const response = await planService.getStudentPlans();
      setPlans(response.data);
    } catch (error) {
      toast.error('Failed to fetch plans');
    } finally {
      setLoading(false);
    }
  };

  const handleCompletePlan = async (planId) => {
    try {
      await planService.completePlan(planId);
      toast.success('Plan marked as completed');
      fetchPlans();
    } catch (error) {
      toast.error('Failed to complete plan');
    }
  };

  const handleDeletePlan = async (planId) => {
    if (window.confirm('Are you sure you want to delete this plan?')) {
      try {
        await planService.deletePlan(planId);
        toast.success('Plan deleted');
        fetchPlans();
      } catch (error) {
        toast.error('Failed to delete plan');
      }
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  return (
    <div className="container" style={{ marginTop: '30px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>Study Planner</h1>
        <button onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancel' : '+ New Plan'}
        </button>
      </div>

      {showForm && (
        <div className="card" style={{ marginBottom: '30px' }}>
          <h2>Create Study Plan</h2>
          <form onSubmit={(e) => {
            e.preventDefault();
            toast.info('Form implementation pending');
          }}>
            <label>Subject</label>
            <input type="text" placeholder="Select subject" />

            <label>Topic</label>
            <input type="text" placeholder="Select topic" />

            <label>Planned Date</label>
            <input type="datetime-local" />

            <label>Duration (minutes)</label>
            <input type="number" placeholder="60" />

            <label>Notes</label>
            <textarea placeholder="Add notes..."></textarea>

            <button type="submit">Create Plan</button>
          </form>
        </div>
      )}

      <div style={{ display: 'grid', gap: '15px' }}>
        {plans && plans.map(plan => (
          <div key={plan._id} className="card">
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
              <div>
                <h3>{plan.subject?.name}</h3>
                <p><strong>Topic:</strong> {plan.topic?.name}</p>
                <p><strong>Date:</strong> {new Date(plan.plannedDate).toLocaleDateString()}</p>
                <p><strong>Duration:</strong> {plan.duration} minutes</p>
                <span className={`badge badge-${plan.status === 'completed' ? 'success' : 'primary'}`}>
                  {plan.status}
                </span>
              </div>
              <div>
                {plan.status === 'pending' && (
                  <>
                    <button onClick={() => handleCompletePlan(plan._id)} style={{ marginRight: '10px' }}>
                      Mark Complete
                    </button>
                  </>
                )}
                <button onClick={() => handleDeletePlan(plan._id)} style={{ background: '#d32f2f' }}>
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {plans.length === 0 && (
        <div className="alert alert-info">
          No study plans yet. Create one to get started!
        </div>
      )}
    </div>
  );
};

export default StudyPlanner;
