import React, { useState, useEffect } from 'react';
import { questionService, subjectService } from '../services/api';
import { toast } from 'react-toastify';
import styles from '../styles/inlineStyles';

const Questions = () => {
  const [questions, setQuestions] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    subject: '',
    topic: ''
  });
  const [loading, setLoading] = useState(true);
  const [selectedQuestion, setSelectedQuestion] = useState(null);

  useEffect(() => {
    fetchQuestions();
    fetchSubjects();
  }, []);

  const fetchQuestions = async () => {
    try {
      const response = await questionService.getAllQuestions();
      setQuestions(response.data);
    } catch (error) {
      toast.error('Failed to fetch questions');
    } finally {
      setLoading(false);
    }
  };

  const fetchSubjects = async () => {
    try {
      const response = await subjectService.getAllSubjects();
      setSubjects(response.data);
    } catch (error) {
      toast.error('Failed to fetch subjects');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await questionService.createQuestion(formData);
      toast.success('Question posted successfully');
      setFormData({ title: '', description: '', subject: '', topic: '' });
      setShowForm(false);
      fetchQuestions();
    } catch (error) {
      toast.error('Failed to post question');
    }
  };

  if (loading) {
    return (
      <div style={styles.loading}>
        <svg style={styles.spinnerSvg} viewBox="0 0 50 50">
          <circle cx="25" cy="25" r="20" fill="none" stroke="#e6e6e6" strokeWidth="5"></circle>
          <path d="M45 25a20 20 0 0 1-20 20" stroke="#667eea" strokeWidth="5" strokeLinecap="round" fill="none">
            <animateTransform attributeName="transform" type="rotate" from="0 25 25" to="360 25 25" dur="1s" repeatCount="indefinite" />
          </path>
        </svg>
      </div>
    );
  }

  return (
    <div style={{ ...styles.container, marginTop: '30px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>Q&A Forum</h1>
        <button onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancel' : '+ Ask Question'}
        </button>
      </div>

      {showForm && (
        <div style={{ ...styles.card, marginBottom: '30px' }}>
          <h2>Ask a Question</h2>
          <form onSubmit={handleSubmit}>
            <label style={styles.label}>Title</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              required
              style={styles.input}
            />

            <label style={styles.label}>Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              required
              rows="5"
              style={{ ...styles.input, height: '120px' }}
            />

            <label style={styles.label}>Subject</label>
            <select
              value={formData.subject}
              onChange={(e) => setFormData({...formData, subject: e.target.value})}
              required
              style={styles.input}
            >
              <option value="">Select a subject</option>
              {subjects.map(subject => (
                <option key={subject._id} value={subject._id}>
                  {subject.name}
                </option>
              ))}
            </select>

            <button type="submit" style={styles.button}>Post Question</button>
          </form>
        </div>
      )}

      <div style={{ display: 'grid', gap: '15px' }}>
        {questions && questions.map(question => (
          <div
            key={question._id}
            style={{ ...styles.card, cursor: 'pointer' }}
            onClick={() => setSelectedQuestion(question._id)}
          >
            <div>
              <h3>{question.title}</h3>
              <p>{question.description.substring(0, 150)}...</p>
              <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <span style={{ ...styles.badge, ...(question.status === 'answered' ? styles.badgeSuccess : styles.badgePrimary) }}>
                  {question.status}
                </span>
                <span style={{ ...styles.badge, ...styles.badgePrimary }}>
                  üë§ {question.asker.name}
                </span>
                <span style={{ ...styles.badge, ...styles.badgePrimary }}>
                  üëÅÔ∏è {question.views} views
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {questions.length === 0 && (
        <div className="alert alert-info">
          No questions yet. Be the first to ask!
        </div>
      )}
    </div>
  );
};

export default Questions;
